import { Text,  ScrollView,View, ImageBackground,TouchableOpacity, Button , SafeAreaView, Alert,} from 'react-native';
import styles from './style';

// import logoHome from '../../assets/logoHome.png';


// const Separator = () => (
//   <View style={styles.separator} />
// );


componentDid




function Loja({navigation}){
   
  return (
      <>
      <SafeAreaView style={styles.container}>

        <View>
          <Text style={styles.title}>
            Seja bem vindo! 
          </Text>
          {/* <Button
            title="Press me"
            onPress={() => Alert.alert('Simple Button pressed')}
          /> */}
        </View>
        
      
        <View>
          <Text style={styles.title}>
            Aqui temos descontos  para programadores !!
          </Text>
           {/* <ImageBackground source={Fundo} style={styles.fundo}> */}
      {/* <ImageBackground style={styles.img} source={logoHome}> */}
        
        <View> 
        {/*           
          <TouchableOpacity style={styles.btn} onPress={()=>{navigation.navigate('Login')}}>

            <Text style={styles.texto}> JOGAR </Text>
          </TouchableOpacity> */}

        </View>
        
            {/* </ImageBackground> */}

          <View style={styles.fixToText}>
            <Button
              title="Produtos"
              // onPress={() => Alert.alert('Left button pressed')}
              onPress={()=>{navigation.navigate('Game')}}
            />
            <Button
              title="Login"
              onPress={()=>{navigation.navigate('Login')}}
            />
          </View>
        </View>

        
      </SafeAreaView>
      </>
    // </View>
  )


  
}
export default Loja;
