import React from 'react';
import { Text, View, ImageBackground, StyleSheet, Image, TouchableOpacity, SafeAreaView, VirtualizedList, StatusBar } from 'react-native';

import backfavela from '../assets/backfavela.jpg'
import helio from '../assets/helio.png'


const DATA = [];

const getItem = (data, index) => ({
  id: Math.random().toString(12).substring(0),
  title: `Item ${index+1}`
});

const getItemCount = (data) => 10;

const Item = ({ title }) => (
  <View style={style.item}>
    <Text style={style.title}>{title}</Text>
  </View>
);




export default function Game({navigation}){
    return(
        <View style={style.view}>

         <TouchableOpacity style={style.buttonHome} onPress={()=>{navigation.navigate('Home')}} >
                <Text style= {{color : 'white'}} >Home</Text>
            </TouchableOpacity>





        <View>
        <SafeAreaView style={style.container}>
        <TouchableOpacity style={style.buttonHome} onPress={()=>{navigation.navigate('Home')}} >
                <Text style= {{color : 'white'}} >Home</Text>
            </TouchableOpacity>




            <VirtualizedList
                data={DATA}
                initialNumToRender={4}
                renderItem={({ item }) => <Item title={item.title} />}
                keyExtractor={item => item.key}
                getItemCount={getItemCount}
                getItem={getItem}
            />
            </SafeAreaView>
        </View>



            {/* <ImageBackground source={backfavela}style={style.container}imageStyle={style.image}> */}

                {/* <View style={style.personagem}>
                    <Image source={helio}
                        style={style.helio}
                    ></Image>
                </View> */}

                <Text style={style.talk}>
                    Oi tudo bem? Prazer sou o Hélio!
                </Text>

            <TouchableOpacity style={style.button}>
                <Text style= {{color : 'white'}}>Próximo</Text>
            </TouchableOpacity>

            <TouchableOpacity style={style.buttonHome} onPress={()=>{navigation.navigate('Home')}} >
                <Text style= {{color : 'white'}} >Home</Text>
            </TouchableOpacity>

            {/* </ImageBackground> */}

        </View>
    )
}
const style = StyleSheet.create({
    container: {
        flex: 1,
        marginTop: StatusBar.currentHeight,
    },
    item: {
        backgroundColor: '#f9c2ff',
        height: 150,
        justifyContent: 'center',
        marginVertical: 8,
        marginHorizontal: 16,
        padding: 20,
        },
    title: {
        fontSize: 32,
    },


    image : {
        flex : 1,
        resizeMode : 'cover'
    },
    view : {
        flex : 1
    },
    talk : {
        backgroundColor : "white",
        height: "18%",
        width : "100%",
        position : "absolute",
        bottom : 0,
        borderRadius : 25,
        fontSize : 20,
        padding : 20
    },
    button : {
        backgroundColor : "#219EBC",
        width : "20%",
        height : "5%",
        position : "absolute",
        bottom : 15,
        marginLeft : "70%",
        alignItems: 'center',
        justifyContent: 'center',
        borderRadius : 10,        
    },
    buttonHome :{
        backgroundColor : "#219EBC",
        width : "20%",
        height : "5%",
        position : "absolute",
        bottom : "89%",
        marginLeft : "70%",
        alignItems: 'center',
        justifyContent: 'center',
        borderRadius : 10,
    },
    personagem :{
        position : "absolute",
        bottom : 0,
        height : '60%',
        width : '100%',
        paddingLeft : '40%',

    },
    helio :{
        flex : 1,
        resizeMode : 'stretch',
        width : '100%'        
    },
  });
