import React from 'react';
import { Text, View, ImageBackground, StyleSheet, Image, TouchableOpacity ,StatusBar, SafeAreaView, FlatList} from 'react-native';

import backfavela from '../assets/backfavela.jpg'
import helio from '../assets/helio.png'



const DATA = [
    {
      id: 'bd7acbea-c1b1-46c2-aed5-3ad53abb28ba',
      title: 'First Item',
      produto: 'geladeira',
      valor: 'valor',
    },
    {
      id: '3ac68afc-c605-48d3-a4f8-fbd91aa97f63',
      title: 'Second Item',

    },
    {
      id: '58694a0f-3da1-471f-bd96-145571e29d72',
      title: 'Third Item',
    },
    {
        id: '58694a0f-3da1-471f-bd96-145571e29d72',
        title: 'Third Item',
      },
  ];

  const Item = ({ title }) => (
    <View style={styles.item}>
      <Text style={styles.title}>{title}</Text>
      <Text style={styles.produto}>{produto}</Text>

    </View>
  );

  export default function Game({navigation}) {
    const renderItem = ({ item }) => (
      <Item title={item.title} />,
      <Item produto={item.produto} />

    );

    return (
        <View>

        <SafeAreaView style={styles.container}>
            <FlatList
            data={DATA}
            renderItem={renderItem}
            keyExtractor={item => item.id}
            />
        </SafeAreaView>

        <TouchableOpacity style={styles.buttonHome} onPress={()=>{navigation.navigate('Home')}} >
                     <Text style= {{color : 'white'}} >Home</Text>
            </TouchableOpacity>

        </View>

    );
  }

  const styles = StyleSheet.create({
    container: {
      flex: 1,
      marginTop: StatusBar.currentHeight || 0,
    },
    item: {
      backgroundColor: '#f9c2ff',
      padding: 20,
      marginVertical: 8,
      marginHorizontal: 16,
    },
    title: {
      fontSize: 32,
    },
    produto: {
    fontSize: 22,
    },
    buttonHome :{
        backgroundColor : "#244444",
        width : "20%",
        height : "5%",
        position : "absolute",
        bottom : "92%",
        marginLeft : "70%",
        alignItems: 'center',
        justifyContent: 'center',
        borderRadius : 10,
        marginTop: 0,
    },
  });





























