        height: "18%",
        width : "100%",
        position : "absolute",
        bottom : 0,
        borderRadius : 25,
        fontSize : 20,
        padding : 20
    },
    button : {
        backgroundColor : "#219EBC",
        width : "20%",
        height : "5%",
        position : "absolute",
        bottom : 15,
        marginLeft : "70%",
        alignItems: 'center',
        justifyContent: 'center',
        borderRadius : 10,        
    },
    buttonHome :{
        backgroundColor : "#219EBC",
        width : "20%",
        height : "5%",
        position : "absolute",
        bottom : "89%",
        marginLeft : "70%",
        alignItems: 'center',
