import React, { Component } from 'react';
import { Text,  Image, StyleSheet, FlatList ,ScrollView,View, ImageBackground,TouchableOpacity, Button , SafeAreaView, Alert} from 'react-native';

export default class Loja   extends Component {
    constructor() {
        super()
        this.state = {
            dataSource: []
        }
    }

    renderItem = ({ item }) => {
        return(
            <View style={{ flex:1, flexDirection: 'row'}}>
                <Image style={{ width: 100, height: 100 }} 
                source={{ uri: item.image }} />
                <View style={{ flex: 1, justifyContent: 'center' }}>
                    <Text>
                        {item.book_title}
                    </Text>
                    <Text>
                        {item.author}
                    </Text>
                </View>
            </View>
        )

    }

    componentDidMount() {
        const url = 'https://www.json-generator.com/api/json/get/ccLAsEcOSq?indent=1'
        
        fetch(url)
        .then((response) => response.json())
        .then((responseJson) => {
            this.setState({
                dataSource: responseJson.book_arry
            })
        })
        .catch((error) => {
            console.log(error)
        })
    }

    render() {
        return (
            <View>
                <FlatList 
                    data= {this.state.dataSource}
                    renderItem= {this.renderItem}

                />
                    
            </View>
        );
    }

}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#f44444'
    }
});


